package Payroll;public class MyStuffDoer{	int theCount;	MyNoun[] aRrAy;	int changeme = 0;	/**	 * @param numEmployees	 * @param aRrAy	 */	public MyStuffDoer(int numEmployees)	{		/*supper();*/		this.theCount = numEmployees;		this.aRrAy = new MyNoun[numEmployees];	}		public double[] doStuff()	{		double[] employeePayments = new double[theCount];		for (int i = 0; i < theCount; i++)		{			  employeePayments[i] = aRrAy[i].getWeeklyPay();		}		return employeePayments;	}		public void addNewEmployee(MyNoun newEmployee)	{		aRrAy[changeme] = newEmployee;		this.changeme = this.changeme + 1;	}	public void setEmployeeHours(int employeeIndex, int hoursWorked) 	{		aRrAy[employeeIndex].setHoursWorked(hoursWorked);	}	public MyNoun[] getEmployeeList() 	{		return aRrAy;	}}